import React, { useState } from 'react';
import { residentsAPI } from '../services/api';

const InputData = ({ navigateTo }) => {
  const [isLoading, setIsLoading] = useState(false);
  const [guardians, setGuardians] = useState([{ 
    id: Date.now(), 
    name: '', 
    id_number: '', 
    email: '', 
    phone: '', 
    relationship: '', 
    address: '',
    is_primary: true 
  }]);

  const [formData, setFormData] = useState({
    name: '',
    type: 'Opa',
    age: '',
    gender: 'male',
    birth_date: '',
    birth_place: '',
    address: '',
    religion: '',
    join_date: new Date().toISOString().split('T')[0],
    condition: 'Sehat',
    medical_history: '',
    allergies: '',
    smoking: 'Tidak',
    alcohol: 'Tidak',
    functional_walking: 'Mandiri',
    functional_eating: 'Mandiri',
    mental_emotion: 'Stabil',
    mental_consciousness: 'Compos Mentis'
  });

  const addGuardian = () => {
    setGuardians([...guardians, { 
      id: Date.now(), 
      name: '', 
      id_number: '', 
      email: '', 
      phone: '', 
      relationship: '', 
      address: '',
      is_primary: false 
    }]);
  };

  const removeGuardian = (id) => {
    if (guardians.length > 1) {
      setGuardians(guardians.filter(guardian => guardian.id !== id));
    }
  };

  const updateGuardian = (id, field, value) => {
    setGuardians(guardians.map(guardian => 
      guardian.id === id ? { ...guardian, [field]: value } : guardian
    ));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      // Prepare data
      const residentData = {
        ...formData,
        guardians: guardians.filter(g => g.name.trim() !== '') // Only include guardians with names
      };

      await residentsAPI.create(residentData);
      alert('✅ Data penghuni berhasil disimpan!');
      navigateTo('list');
    } catch (error) {
      console.error('Error creating resident:', error);
      alert('❌ Gagal menyimpan data: ' + (error.error || 'Terjadi kesalahan'));
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="page-wrapper">
      <button className="btn btn-back" onClick={() => navigateTo('dashboard')}>
        <i className="fas fa-arrow-left"></i> Kembali
      </button>

      <h2 className="page-title">
        <i className="fas fa-user-plus"></i>
        Form Input Data Oma & Opa
      </h2>

      <form onSubmit={handleSubmit} id="inputForm">
        {/* Data Pribadi */}
        <div className="form-section">
          <h4><i className="fas fa-user"></i> Data Pribadi</h4>
          <div className="row g-3">
            <div className="col-md-6">
              <label className="form-label">Nama Lengkap *</label>
              <input 
                type="text" 
                className="form-control" 
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="Masukkan nama lengkap" 
                required 
                disabled={isLoading}
              />
            </div>
            <div className="col-md-6">
              <label className="form-label">Umur *</label>
              <input 
                type="number" 
                className="form-control" 
                name="age"
                value={formData.age}
                onChange={handleChange}
                placeholder="Masukkan umur" 
                required 
                min="1"
                max="120"
                disabled={isLoading}
              />
            </div>
            <div className="col-md-6">
              <label className="form-label">Jenis Kelamin *</label>
              <select 
                className="form-select" 
                name="gender"
                value={formData.gender}
                onChange={handleChange}
                required
                disabled={isLoading}
              >
                <option value="male">Laki-laki (Opa)</option>
                <option value="female">Perempuan (Oma)</option>
              </select>
            </div>
            <div className="col-md-6">
              <label className="form-label">Tipe *</label>
              <select 
                className="form-select" 
                name="type"
                value={formData.type}
                onChange={handleChange}
                required
                disabled={isLoading}
              >
                <option value="Opa">Opa</option>
                <option value="Oma">Oma</option>
              </select>
            </div>
            <div className="col-md-6">
              <label className="form-label">Tanggal Lahir</label>
              <input 
                type="date" 
                className="form-control" 
                name="birth_date"
                value={formData.birth_date}
                onChange={handleChange}
                disabled={isLoading}
              />
            </div>
            <div className="col-md-6">
              <label className="form-label">Tempat Lahir</label>
              <input 
                type="text" 
                className="form-control" 
                name="birth_place"
                value={formData.birth_place}
                onChange={handleChange}
                placeholder="Tempat lahir"
                disabled={isLoading}
              />
            </div>
            <div className="col-12">
              <label className="form-label">Alamat</label>
              <textarea 
                className="form-control" 
                name="address"
                value={formData.address}
                onChange={handleChange}
                rows="2"
                placeholder="Alamat lengkap"
                disabled={isLoading}
              ></textarea>
            </div>
            <div className="col-md-6">
              <label className="form-label">Agama</label>
              <select 
                className="form-select" 
                name="religion"
                value={formData.religion}
                onChange={handleChange}
                disabled={isLoading}
              >
                <option value="">Pilih Agama</option>
                <option value="Islam">Islam</option>
                <option value="Kristen">Kristen</option>
                <option value="Katolik">Katolik</option>
                <option value="Hindu">Hindu</option>
                <option value="Buddha">Buddha</option>
                <option value="Konghucu">Konghucu</option>
              </select>
            </div>
            <div className="col-md-6">
              <label className="form-label">Tanggal Masuk *</label>
              <input 
                type="date" 
                className="form-control" 
                name="join_date"
                value={formData.join_date}
                onChange={handleChange}
                required
                disabled={isLoading}
              />
            </div>
            <div className="col-md-6">
              <label className="form-label">Kondisi Umum *</label>
              <select 
                className="form-select" 
                name="condition"
                value={formData.condition}
                onChange={handleChange}
                required
                disabled={isLoading}
              >
                <option value="Sehat">Sehat</option>
                <option value="Cukup Sehat">Cukup Sehat</option>
                <option value="Kurang Sehat">Kurang Sehat</option>
              </select>
            </div>
          </div>
        </div>

        {/* Data Kesehatan */}
        <div className="form-section">
          <h4><i className="fas fa-heartbeat"></i> Data Kesehatan</h4>
          <div className="row g-3">
            <div className="col-12">
              <label className="form-label">Riwayat Penyakit</label>
              <textarea 
                className="form-control" 
                name="medical_history"
                value={formData.medical_history}
                onChange={handleChange}
                rows="2"
                placeholder="Contoh: Diabetes, Hipertensi, Asam Urat"
                disabled={isLoading}
              ></textarea>
            </div>
            <div className="col-md-6">
              <label className="form-label">Alergi</label>
              <input 
                type="text" 
                className="form-control" 
                name="allergies"
                value={formData.allergies}
                onChange={handleChange}
                placeholder="Contoh: Seafood, Udang, Debu"
                disabled={isLoading}
              />
            </div>
            <div className="col-md-6">
              <label className="form-label">Merokok</label>
              <select 
                className="form-select" 
                name="smoking"
                value={formData.smoking}
                onChange={handleChange}
                disabled={isLoading}
              >
                <option value="Tidak">Tidak</option>
                <option value="Ya">Ya</option>
                <option value="Dulu (Sudah Berhenti)">Dulu (Sudah Berhenti)</option>
              </select>
            </div>
            <div className="col-md-6">
              <label className="form-label">Minum Alkohol</label>
              <select 
                className="form-select" 
                name="alcohol"
                value={formData.alcohol}
                onChange={handleChange}
                disabled={isLoading}
              >
                <option value="Tidak">Tidak</option>
                <option value="Ya">Ya</option>
                <option value="Jarang">Jarang</option>
              </select>
            </div>
          </div>
        </div>

        {/* Data Wali */}
        <div id="guardian-container">
          {guardians.map((guardian, index) => (
            <div key={guardian.id} className="guardian-section">
              <div className="d-flex justify-content-between align-items-center mb-3">
                <h5><i className="fas fa-user-friends"></i> Data Wali {index + 1}</h5>
                {guardians.length > 1 && (
                  <button 
                    type="button" 
                    className="btn btn-sm btn-danger"
                    onClick={() => removeGuardian(guardian.id)}
                    disabled={isLoading}
                  >
                    <i className="fas fa-times"></i> Hapus
                  </button>
                )}
              </div>
              <div className="row g-3">
                <div className="col-md-6">
                  <label className="form-label">Nama Wali *</label>
                  <input 
                    type="text" 
                    className="form-control" 
                    value={guardian.name}
                    onChange={(e) => updateGuardian(guardian.id, 'name', e.target.value)}
                    placeholder="Nama lengkap wali" 
                    required
                    disabled={isLoading}
                  />
                </div>
                <div className="col-md-6">
                  <label className="form-label">Nomor Telepon *</label>
                  <input 
                    type="tel" 
                    className="form-control" 
                    value={guardian.phone}
                    onChange={(e) => updateGuardian(guardian.id, 'phone', e.target.value)}
                    placeholder="081234567890"
                    required
                    disabled={isLoading}
                  />
                </div>
                <div className="col-md-6">
                  <label className="form-label">Hubungan</label>
                  <input 
                    type="text" 
                    className="form-control" 
                    value={guardian.relationship}
                    onChange={(e) => updateGuardian(guardian.id, 'relationship', e.target.value)}
                    placeholder="Contoh: Anak, Saudara, Keponakan"
                    disabled={isLoading}
                  />
                </div>
                <div className="col-md-6">
                  <label className="form-label">Email</label>
                  <input 
                    type="email" 
                    className="form-control" 
                    value={guardian.email}
                    onChange={(e) => updateGuardian(guardian.id, 'email', e.target.value)}
                    placeholder="email@contoh.com"
                    disabled={isLoading}
                  />
                </div>
                <div className="col-12">
                  <div className="form-check">
                    <input 
                      type="checkbox" 
                      className="form-check-input" 
                      id={`primary-${guardian.id}`}
                      checked={guardian.is_primary}
                      onChange={(e) => updateGuardian(guardian.id, 'is_primary', e.target.checked)}
                      disabled={isLoading}
                    />
                    <label className="form-check-label" htmlFor={`primary-${guardian.id}`}>
                      Wali Utama
                    </label>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="text-center mt-4">
          <button 
            type="button" 
            className="btn btn-secondary me-2"
            onClick={addGuardian}
            disabled={isLoading}
          >
            <i className="fas fa-plus"></i> Tambah Wali Lain
          </button>
        </div>

        <div className="text-center mt-4">
          <button 
            type="submit" 
            className="btn btn-primary-custom me-3"
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                Menyimpan...
              </>
            ) : (
              <>
                <i className="fas fa-save"></i> Simpan Data
              </>
            )}
          </button>
          <button 
            type="button" 
            className="btn btn-secondary-custom" 
            onClick={() => navigateTo('dashboard')}
            disabled={isLoading}
          >
            <i className="fas fa-times"></i> Batal
          </button>
        </div>
      </form>
    </div>
  );
};

export default InputData;
