// components/Donation.js
import React, { useState, useEffect } from 'react';
import { transactionsAPI, donationCategoriesAPI } from '../services/api';

const Donation = ({ navigateTo }) => {
  const [showIncomeForm, setShowIncomeForm] = useState(false);
  const [showExpenseForm, setShowExpenseForm] = useState(false);
  const [transactions, setTransactions] = useState([]);
  const [categories, setCategories] = useState([]);
  const [stats, setStats] = useState({
    totalIncome: 0,
    totalExpense: 0,
    balance: 0,
    monthlyIncome: 0,
    monthlyExpense: 0
  });
  const [isLoading, setIsLoading] = useState(false);
  const [isFormLoading, setIsFormLoading] = useState(false);
  
  const [filters, setFilters] = useState({
    type: '',
    month: new Date().toISOString().slice(0, 7),
    category: ''
  });

  const [incomeForm, setIncomeForm] = useState({
    category_id: '',
    amount: '',
    transaction_date: new Date().toISOString().slice(0, 10),
    source: '',
    description: '',
    payment_method: 'cash',
    reference_number: '',
    notes: ''
  });

  const [expenseForm, setExpenseForm] = useState({
    category_id: '',
    amount: '',
    transaction_date: new Date().toISOString().slice(0, 10),
    source: '',
    description: '',
    payment_method: 'cash',
    reference_number: '',
    notes: ''
  });

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    fetchTransactions();
    fetchFinancialSummary();
  }, [filters]);

  const fetchData = async () => {
    try {
      const categoriesData = await donationCategoriesAPI.getAll();
      setCategories(categoriesData);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const fetchTransactions = async () => {
    try {
      setIsLoading(true);
      const data = await transactionsAPI.getAll(filters);
      setTransactions(data);
    } catch (error) {
      console.error('Error fetching transactions:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const fetchFinancialSummary = async () => {
    try {
      const currentYear = new Date().getFullYear();
      const data = await transactionsAPI.getFinancialSummary(currentYear);
      setStats({
        totalIncome: data.summary.total_income || 0,
        totalExpense: data.summary.total_expense || 0,
        balance: data.summary.balance || 0,
        monthlyIncome: data.monthly_breakdown.find(m => m.month === filters.month)?.income || 0,
        monthlyExpense: data.monthly_breakdown.find(m => m.month === filters.month)?.expense || 0
      });
    } catch (error) {
      console.error('Error fetching financial summary:', error);
    }
  };

  const toggleForm = (formType) => {
    if (formType === 'income') {
      setShowIncomeForm(!showIncomeForm);
      setShowExpenseForm(false);
      if (!showIncomeForm) {
        // Reset form and set default category
        const incomeCategory = categories.find(c => c.type === 'income');
        setIncomeForm({
          category_id: incomeCategory?.id || '',
          amount: '',
          transaction_date: new Date().toISOString().slice(0, 10),
          source: '',
          description: '',
          payment_method: 'cash',
          reference_number: '',
          notes: ''
        });
      }
    } else if (formType === 'expense') {
      setShowExpenseForm(!showExpenseForm);
      setShowIncomeForm(false);
      if (!showExpenseForm) {
        // Reset form and set default category
        const expenseCategory = categories.find(c => c.type === 'expense');
        setExpenseForm({
          category_id: expenseCategory?.id || '',
          amount: '',
          transaction_date: new Date().toISOString().slice(0, 10),
          source: '',
          description: '',
          payment_method: 'cash',
          reference_number: '',
          notes: ''
        });
      }
    }
  };

  const handleIncomeChange = (e) => {
    const { name, value } = e.target;
    setIncomeForm(prev => ({ ...prev, [name]: value }));
  };

  const handleExpenseChange = (e) => {
    const { name, value } = e.target;
    setExpenseForm(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmitIncome = async (e) => {
    e.preventDefault();
    setIsFormLoading(true);

    try {
      await transactionsAPI.create(incomeForm);
      alert('✅ Data pemasukan berhasil disimpan!');
      setShowIncomeForm(false);
      fetchTransactions();
      fetchFinancialSummary();
    } catch (error) {
      console.error('Error creating income:', error);
      alert('❌ Gagal menyimpan pemasukan: ' + (error.error || 'Terjadi kesalahan'));
    } finally {
      setIsFormLoading(false);
    }
  };

  const handleSubmitExpense = async (e) => {
    e.preventDefault();
    setIsFormLoading(true);

    try {
      await transactionsAPI.create(expenseForm);
      alert('✅ Data pengeluaran berhasil disimpan!');
      setShowExpenseForm(false);
      fetchTransactions();
      fetchFinancialSummary();
    } catch (error) {
      console.error('Error creating expense:', error);
      alert('❌ Gagal menyimpan pengeluaran: ' + (error.error || 'Terjadi kesalahan'));
    } finally {
      setIsFormLoading(false);
    }
  };

  const handleFilterChange = (field, value) => {
    setFilters(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('id-ID', {
      day: 'numeric',
      month: 'long',
      year: 'numeric'
    });
  };

  const incomeCategories = categories.filter(c => c.type === 'income');
  const expenseCategories = categories.filter(c => c.type === 'expense');

  return (
    <div className="page-wrapper">
      <button className="btn btn-back" onClick={() => navigateTo('dashboard')}>
        <i className="fas fa-arrow-left"></i> Kembali
      </button>

      <h2 className="page-title">
        <i className="fas fa-hand-holding-heart"></i>
        Kelola Sumbangan
      </h2>

      {/* Statistics */}
      <div className="stats-grid mb-4">
        <div className="stat-card">
          <div className="stat-label">Total Pemasukan</div>
          <div className="stat-value text-success">Rp {stats.totalIncome.toLocaleString('id-ID')}</div>
          <div className="stat-sublabel">Bulan ini: Rp {stats.monthlyIncome.toLocaleString('id-ID')}</div>
        </div>
        <div className="stat-card">
          <div className="stat-label">Total Pengeluaran</div>
          <div className="stat-value text-danger">Rp {stats.totalExpense.toLocaleString('id-ID')}</div>
          <div className="stat-sublabel">Bulan ini: Rp {stats.monthlyExpense.toLocaleString('id-ID')}</div>
        </div>
        <div className="stat-card">
          <div className="stat-label">Saldo Tersisa</div>
          <div className="stat-value text-primary">Rp {stats.balance.toLocaleString('id-ID')}</div>
          <div className="stat-sublabel">Per {new Date().toLocaleDateString('id-ID')}</div>
        </div>
      </div>

      <div className="mb-4">
        <button 
          className="btn btn-success me-2" 
          onClick={() => toggleForm('income')}
          disabled={isFormLoading}
        >
          <i className="fas fa-plus"></i> Tambah Pemasukan
        </button>
        <button 
          className="btn btn-danger" 
          onClick={() => toggleForm('expense')}
          disabled={isFormLoading}
        >
          <i className="fas fa-minus"></i> Tambah Pengeluaran
        </button>
      </div>

      {/* Income Form */}
      {showIncomeForm && (
        <div className="form-section mb-4">
          <h4><i className="fas fa-arrow-up text-success"></i> Form Pemasukan</h4>
          <form onSubmit={handleSubmitIncome}>
            <div className="row g-3">
              <div className="col-md-6">
                <label className="form-label">Tanggal *</label>
                <input 
                  type="date" 
                  className="form-control" 
                  name="transaction_date"
                  value={incomeForm.transaction_date}
                  onChange={handleIncomeChange}
                  required 
                  disabled={isFormLoading}
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Jumlah (Rp) *</label>
                <input 
                  type="number" 
                  className="form-control" 
                  name="amount"
                  value={incomeForm.amount}
                  onChange={handleIncomeChange}
                  placeholder="0" 
                  required 
                  min="1"
                  disabled={isFormLoading}
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Kategori *</label>
                <select 
                  className="form-select" 
                  name="category_id"
                  value={incomeForm.category_id}
                  onChange={handleIncomeChange}
                  required
                  disabled={isFormLoading}
                >
                  <option value="">-- Pilih Kategori --</option>
                  {incomeCategories.map(category => (
                    <option key={category.id} value={category.id}>
                      {category.name}
                    </option>
                  ))}
                </select>
              </div>
              <div className="col-md-6">
                <label className="form-label">Sumber/Donatur *</label>
                <input 
                  type="text" 
                  className="form-control" 
                  name="source"
                  value={incomeForm.source}
                  onChange={handleIncomeChange}
                  placeholder="Nama donatur" 
                  required
                  disabled={isFormLoading}
                />
              </div>
              <div className="col-12">
                <label className="form-label">Keterangan</label>
                <textarea 
                  className="form-control" 
                  name="description"
                  value={incomeForm.description}
                  onChange={handleIncomeChange}
                  rows="3" 
                  placeholder="Deskripsi lengkap..."
                  disabled={isFormLoading}
                ></textarea>
              </div>
              <div className="col-md-6">
                <label className="form-label">Metode Pembayaran</label>
                <select 
                  className="form-select" 
                  name="payment_method"
                  value={incomeForm.payment_method}
                  onChange={handleIncomeChange}
                  disabled={isFormLoading}
                >
                  <option value="cash">Tunai</option>
                  <option value="transfer">Transfer Bank</option>
                  <option value="check">Cek</option>
                  <option value="other">Lainnya</option>
                </select>
              </div>
              <div className="col-md-6">
                <label className="form-label">Nomor Referensi</label>
                <input 
                  type="text" 
                  className="form-control" 
                  name="reference_number"
                  value={incomeForm.reference_number}
                  onChange={handleIncomeChange}
                  placeholder="Nomor bukti transfer/cek"
                  disabled={isFormLoading}
                />
              </div>
            </div>
            <div className="text-center mt-4">
              <button 
                type="submit" 
                className="btn btn-success me-2"
                disabled={isFormLoading}
              >
                {isFormLoading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2"></span>
                    Menyimpan...
                  </>
                ) : (
                  <>
                    <i className="fas fa-save"></i> Simpan
                  </>
                )}
              </button>
              <button 
                type="button" 
                className="btn btn-secondary" 
                onClick={() => toggleForm('income')}
                disabled={isFormLoading}
              >
                <i className="fas fa-times"></i> Batal
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Expense Form */}
      {showExpenseForm && (
        <div className="form-section mb-4">
          <h4><i className="fas fa-arrow-down text-danger"></i> Form Pengeluaran</h4>
          <form onSubmit={handleSubmitExpense}>
            <div className="row g-3">
              <div className="col-md-6">
                <label className="form-label">Tanggal *</label>
                <input 
                  type="date" 
                  className="form-control" 
                  name="transaction_date"
                  value={expenseForm.transaction_date}
                  onChange={handleExpenseChange}
                  required 
                  disabled={isFormLoading}
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Jumlah (Rp) *</label>
                <input 
                  type="number" 
                  className="form-control" 
                  name="amount"
                  value={expenseForm.amount}
                  onChange={handleExpenseChange}
                  placeholder="0" 
                  required 
                  min="1"
                  disabled={isFormLoading}
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Kategori *</label>
                <select 
                  className="form-select" 
                  name="category_id"
                  value={expenseForm.category_id}
                  onChange={handleExpenseChange}
                  required
                  disabled={isFormLoading}
                >
                  <option value="">-- Pilih Kategori --</option>
                  {expenseCategories.map(category => (
                    <option key={category.id} value={category.id}>
                      {category.name}
                    </option>
                  ))}
                </select>
              </div>
              <div className="col-md-6">
                <label className="form-label">Tujuan/Keperluan *</label>
                <input 
                  type="text" 
                  className="form-control" 
                  name="source"
                  value={expenseForm.source}
                  onChange={handleExpenseChange}
                  placeholder="Untuk apa pengeluaran ini" 
                  required
                  disabled={isFormLoading}
                />
              </div>
              <div className="col-12">
                <label className="form-label">Keterangan</label>
                <textarea 
                  className="form-control" 
                  name="description"
                  value={expenseForm.description}
                  onChange={handleExpenseChange}
                  rows="3" 
                  placeholder="Deskripsi lengkap..."
                  disabled={isFormLoading}
                ></textarea>
              </div>
              <div className="col-md-6">
                <label className="form-label">Metode Pembayaran</label>
                <select 
                  className="form-select" 
                  name="payment_method"
                  value={expenseForm.payment_method}
                  onChange={handleExpenseChange}
                  disabled={isFormLoading}
                >
                  <option value="cash">Tunai</option>
                  <option value="transfer">Transfer Bank</option>
                  <option value="check">Cek</option>
                  <option value="other">Lainnya</option>
                </select>
              </div>
              <div className="col-md-6">
                <label className="form-label">Nomor Referensi</label>
                <input 
                  type="text" 
                  className="form-control" 
                  name="reference_number"
                  value={expenseForm.reference_number}
                  onChange={handleExpenseChange}
                  placeholder="Nomor bukti pembayaran"
                  disabled={isFormLoading}
                />
              </div>
            </div>
            <div className="text-center mt-4">
              <button 
                type="submit" 
                className="btn btn-danger me-2"
                disabled={isFormLoading}
              >
                {isFormLoading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2"></span>
                    Menyimpan...
                  </>
                ) : (
                  <>
                    <i className="fas fa-save"></i> Simpan
                  </>
                )}
              </button>
              <button 
                type="button" 
                className="btn btn-secondary" 
                onClick={() => toggleForm('expense')}
                disabled={isFormLoading}
              >
                <i className="fas fa-times"></i> Batal
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Filters */}
      <div className="filter-row mb-4">
        <select 
          className="form-select"
          value={filters.type}
          onChange={(e) => handleFilterChange('type', e.target.value)}
          disabled={isLoading}
        >
          <option value="">Semua Transaksi</option>
          <option value="income">Pemasukan</option>
          <option value="expense">Pengeluaran</option>
        </select>
        <input 
          type="month" 
          className="form-control"
          value={filters.month}
          onChange={(e) => handleFilterChange('month', e.target.value)}
          disabled={isLoading}
        />
        <select 
          className="form-select"
          value={filters.category}
          onChange={(e) => handleFilterChange('category', e.target.value)}
          disabled={isLoading}
        >
          <option value="">Semua Kategori</option>
          {categories.map(category => (
            <option key={category.id} value={category.id}>
              {category.name}
            </option>
          ))}
        </select>
        <button 
          className="btn btn-outline-secondary"
          onClick={() => setFilters({ type: '', month: new Date().toISOString().slice(0, 7), category: '' })}
          disabled={isLoading}
        >
          <i className="fas fa-times"></i> Reset
        </button>
      </div>

      <h4 className="mb-4">Riwayat Transaksi</h4>

      {/* Transaction List */}
      <div>
        {isLoading ? (
          <div className="text-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="mt-2">Memuat data transaksi...</p>
          </div>
        ) : transactions.length === 0 ? (
          <div className="text-center py-5">
            <i className="fas fa-receipt fa-3x text-muted mb-3"></i>
            <p className="text-muted">
              {Object.values(filters).some(f => f !== '' && f !== new Date().toISOString().slice(0, 7)) 
                ? 'Tidak ditemukan transaksi dengan filter tersebut' 
                : 'Belum ada transaksi. Tambahkan pemasukan atau pengeluaran untuk melihatnya di sini.'
              }
            </p>
          </div>
        ) : (
          transactions.map(transaction => (
            <div 
              key={transaction.id} 
              className={`transaction-item ${transaction.transaction_type === 'expense' ? 'expense' : ''}`}
            >
              <div className="transaction-header">
                <div className="transaction-info">
                  <div className="transaction-title">
                    <i className={`fas fa-arrow-${transaction.transaction_type === 'income' ? 'up text-success' : 'down text-danger'}`}></i>
                    {transaction.source}
                  </div>
                  <div className="transaction-meta">
                    {formatDate(transaction.transaction_date)} • 
                    ID: {transaction.transaction_id} • 
                    Kategori: {transaction.category_name}
                  </div>
                  {transaction.description && (
                    <p style={{ margin: '10px 0 0 0', color: '#666' }}>{transaction.description}</p>
                  )}
                  <div className="transaction-meta mt-2">
                    <small>
                      <i className="fas fa-wallet me-1"></i>
                      {transaction.payment_method} 
                      {transaction.reference_number && ` • Ref: ${transaction.reference_number}`}
                    </small>
                  </div>
                </div>
                <div className={`transaction-amount amount-${transaction.transaction_type}`}>
                  {transaction.transaction_type === 'income' ? '+' : '-'} 
                  Rp {parseFloat(transaction.amount).toLocaleString('id-ID')}
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Donation;
