// components/Dashboard.js
import React, { useState, useEffect } from 'react';
import { dashboardAPI } from '../services/api';

const Dashboard = ({ navigateTo, user, onLogout }) => {
  const [stats, setStats] = useState({
    active_residents: 0,
    today_records: 0,
    monthly_income: 0,
    monthly_expense: 0
  });
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetchDashboardStats();
  }, []);

  const fetchDashboardStats = async () => {
    try {
      const data = await dashboardAPI.getStats();
      setStats(data);
    } catch (error) {
      console.error('Error fetching dashboard stats:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const menuItems = [
    {
      icon: 'fas fa-user-plus',
      title: 'Input Data Oma & Opa',
      description: 'Tambahkan data penghuni baru ke dalam sistem',
      page: 'input'
    },
    {
      icon: 'fas fa-users',
      title: 'Daftar Opa & Oma',
      description: 'Lihat daftar semua penghuni panti',
      page: 'list'
    },
    {
      icon: 'fas fa-clipboard-list',
      title: 'Record Harian Opa & Oma',
      description: 'Catat aktivitas dan kondisi harian',
      page: 'record'
    },
    {
      icon: 'fas fa-hand-holding-heart',
      title: 'Kelola Dana & Inventory',
      description: 'Kelola pemasukan dan pengeluaran dana',
      page: 'donation'
    }
  ];

  if (isLoading) {
    return (
      <div className="page-wrapper text-center py-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="page-wrapper" style={{ background: 'white', borderRadius: '12px', padding: '30px', boxShadow: '0 4px 12px rgba(0,0,0,0.05)' }}>
      {/* Header with User Info */}
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h1><i className="fas fa-heartbeat"></i> Panti Werdha Kasih Karunia</h1>
          <p className="subtitle">Sistem Monitoring Kesehatan & Kesejahteraan Penghuni</p>
        </div>
        <div className="text-end">
          <div className="user-info mb-2">
            <i className="fas fa-user-circle me-2"></i>
            <strong>{user?.full_name || 'Admin'}</strong>
            <span className="badge bg-info ms-2">{user?.role || 'Admin'}</span>
          </div>
          <button 
            className="btn btn-sm btn-outline-danger"
            onClick={onLogout}
          >
            <i className="fas fa-sign-out-alt"></i> Logout
          </button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="row mb-4">
        <div className="col-md-3">
          <div className="stat-card bg-primary text-white">
            <div className="stat-icon">
              <i className="fas fa-users"></i>
            </div>
            <div className="stat-content">
              <h3>{stats.active_residents}</h3>
              <p>Penghuni Aktif</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="stat-card bg-success text-white">
            <div className="stat-icon">
              <i className="fas fa-clipboard-check"></i>
            </div>
            <div className="stat-content">
              <h3>{stats.today_records}</h3>
              <p>Record Hari Ini</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="stat-card bg-warning text-dark">
            <div className="stat-icon">
              <i className="fas fa-arrow-up"></i>
            </div>
            <div className="stat-content">
              <h3>Rp {stats.monthly_income.toLocaleString('id-ID')}</h3>
              <p>Pemasukan Bulan Ini</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="stat-card bg-danger text-white">
            <div className="stat-icon">
              <i className="fas fa-arrow-down"></i>
            </div>
            <div className="stat-content">
              <h3>Rp {stats.monthly_expense.toLocaleString('id-ID')}</h3>
              <p>Pengeluaran Bulan Ini</p>
            </div>
          </div>
        </div>
      </div>

      {/* Menu Grid */}
      <div className="menu-grid">
        {menuItems.map((item, index) => (
          <div 
            key={index}
            className="menu-item" 
            onClick={() => navigateTo(item.page)}
            style={{
              background: 'white',
              borderRadius: '12px',
              padding: '30px 25px',
              textAlign: 'center',
              cursor: 'pointer',
              transition: 'all 0.3s ease',
              color: '#333',
              position: 'relative',
              overflow: 'hidden',
              border: '1px solid #eaeaea',
              boxShadow: '0 2px 8px rgba(0,0,0,0.05)'
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.transform = 'translateY(-5px)';
              e.currentTarget.style.boxShadow = '0 8px 20px rgba(0,0,0,0.1)';
              e.currentTarget.style.borderColor = '#0056a6';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.transform = 'translateY(0)';
              e.currentTarget.style.boxShadow = '0 2px 8px rgba(0,0,0,0.05)';
              e.currentTarget.style.borderColor = '#eaeaea';
            }}
          >
            <i 
              className={item.icon} 
              style={{
                fontSize: '3.5rem',
                marginBottom: '20px',
                display: 'block',
                color: '#0056a6'
              }}
            ></i>
            <h3 style={{
              fontSize: '1.4rem',
              fontWeight: '600',
              marginBottom: '10px',
              color: '#0056a6'
            }}>{item.title}</h3>
            <p style={{
              fontSize: '0.95rem',
              color: '#6c757d',
              margin: 0
            }}>{item.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
