// client/src/App.js
import React, { useState, useEffect, createContext } from 'react';
import Dashboard from './components/Dashboard';
import InputData from './components/InputData';
import ResidentList from './components/ResidentList';
import ResidentDetail from './components/ResidentDetail';
import Record from './components/Record';
import Donation from './components/Donation';
import Login from './components/Login';
import LoadingSpinner from './components/LoadingSpinner';
import './App.css';

// Create API context
export const APIContext = createContext();

function App() {
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [selectedResidentId, setSelectedResidentId] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [user, setUser] = useState(null);

  // Check authentication on mount
  useEffect(() => {
    const token = localStorage.getItem('token');
    const savedUser = localStorage.getItem('user');

    if (token && savedUser) {
      setIsAuthenticated(true);
      setUser(JSON.parse(savedUser));
    }
    setIsLoading(false);
  }, []);

  const navigateTo = (page) => {
    setCurrentPage(page);
  };

  const showDetail = (residentId) => {
    setSelectedResidentId(residentId);
    setCurrentPage('detail');
  };

  const handleLogin = (userData, token) => {
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(userData));
    setIsAuthenticated(true);
    setUser(userData);
    setCurrentPage('dashboard');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsAuthenticated(false);
    setUser(null);
    setCurrentPage('dashboard');
  };

  const renderPage = () => {
    if (!isAuthenticated) {
      return <Login onLogin={handleLogin} />;
    }

    switch (currentPage) {
      case 'dashboard':
        return <Dashboard navigateTo={navigateTo} user={user} onLogout={handleLogout} />;
      case 'input':
        return <InputData navigateTo={navigateTo} />;
      case 'list':
        return <ResidentList navigateTo={navigateTo} showDetail={showDetail} />;
      case 'detail':
        return <ResidentDetail navigateTo={navigateTo} residentId={selectedResidentId} />;
      case 'record':
        return <Record navigateTo={navigateTo} />;
      case 'donation':
        return <Donation navigateTo={navigateTo} />;
      default:
        return <Dashboard navigateTo={navigateTo} user={user} onLogout={handleLogout} />;
    }
  };

  if (isLoading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="container-fluid">
      {renderPage()}
    </div>
  );
}

export default App;
