// client/src/components/ResidentList.js
import React, { useState, useEffect } from 'react';
import { residentsAPI } from '../services/api';
import { calculateAge, formatDate } from '../utils/helpers';

const ResidentList = ({ navigateTo, showDetail }) => {
  const [residents, setResidents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [deletingId, setDeletingId] = useState(null);

  useEffect(() => {
    fetchResidents();
  }, []);

  const fetchResidents = async () => {
    try {
      // Use the API service instead of direct axios
      const data = await residentsAPI.getAll();
      setResidents(data);
    } catch (error) {
      console.error('Error fetching residents:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteResident = async (id, name) => {
    if (!window.confirm(`Yakin ingin menghapus data ${name}? \n\nâš ï¸ Data yang dihapus tidak dapat dikembalikan!`)) {
      return;
    }

    try {
      setDeletingId(id);
      await residentsAPI.delete(id);
      
      alert(`âœ… Data ${name} berhasil dihapus`);
      
      // Refresh the list
      fetchResidents();
    } catch (error) {
      console.error('Error deleting resident:', error);
      alert(`âŒ Gagal menghapus data: ${error.error || 'Terjadi kesalahan'}`);
    } finally {
      setDeletingId(null);
    }
  };

  // Filter residents based on search term
  const filteredResidents = residents.filter(resident => 
    resident.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

 return (
    <div className="page-wrapper">
      <button className="btn btn-back" onClick={() => navigateTo('dashboard')}>
        <i className="fas fa-arrow-left"></i> Kembali
      </button>

      <h2 className="page-title">
        <i className="fas fa-users"></i>
        Daftar Opa & Oma
      </h2>

      <div className="search-box mb-3">
        <input 
          type="text" 
          className="form-control" 
          placeholder="ðŸ” Cari nama Opa atau Oma..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {loading ? (
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">Memuat data...</p>
        </div>
      ) : filteredResidents.length === 0 ? (
        <div className="text-center py-5">
          <i className="fas fa-users fa-3x text-muted mb-3"></i>
          <p className="text-muted">
            {searchTerm ? 'Tidak ada penghuni yang sesuai dengan pencarian' : 'Belum ada data penghuni'}
          </p>
          {!searchTerm && (
            <button 
              className="btn btn-primary-custom mt-3"
              onClick={() => navigateTo('input')}
            >
              <i className="fas fa-user-plus"></i> Tambah Penghuni Baru
            </button>
          )}
        </div>
      ) : (
        <div className="profile-grid">
          {filteredResidents.map(resident => {
            const age = resident.age || calculateAge(resident.birth_date);
            const gender = resident.gender || 'male';
            const genderIcon = gender === 'male' ? 'fa-male' : 'fa-female';
            const type = gender === 'male' ? 'Opa' : 'Oma';
            
            return (
              <div 
                key={resident.id} 
                className="profile-card"
              >
                <div 
                  className="profile-header"
                  onClick={() => showDetail(resident.id)}
                  style={{ cursor: 'pointer' }}
                >
                  <div className="profile-avatar">
                    <i className={`fas ${genderIcon}`}></i>
                  </div>
                  <h5 style={{ margin: 0, fontSize: '1.3rem' }}>{resident.name}</h5>
                  <p style={{ margin: '5px 0 0 0', opacity: 0.9 }}>
                    {type} â€¢ {age || '?'} Tahun â€¢ ID: {resident.resident_id}
                  </p>
                </div>
                <div className="profile-body">
                  <div className="profile-info-item">
                    <i className="fas fa-heartbeat"></i>
                    <span><strong>Kondisi:</strong> {resident.condition || 'Tidak ada data'}</span>
                  </div>
                  <div className="profile-info-item">
                    <i className="fas fa-calendar"></i>
                    <span><strong>Masuk:</strong> {formatDate(resident.join_date) || '-'}</span>
                  </div>
                  <div className="mt-3 d-flex justify-content-between align-items-center">
                    <span className={`badge ${resident.status === 'Perlu Perhatian' ? 'bg-warning text-dark' : 'bg-success'} badge-custom`}>
                      {resident.status || 'Aktif'}
                    </span>
                    
                    <div className="btn-group" role="group">
                      <button 
                        className="btn btn-sm btn-outline-primary"
                        onClick={() => showDetail(resident.id)}
                      >
                        <i className="fas fa-eye"></i> Detail
                      </button>
                      <button 
                        className="btn btn-sm btn-outline-danger"
                        onClick={() => handleDeleteResident(resident.id, resident.name)}
                        disabled={deletingId === resident.id}
                      >
                        {deletingId === resident.id ? (
                          <>
                            <span className="spinner-border spinner-border-sm me-1"></span>
                            Hapus
                          </>
                        ) : (
                          <>
                            <i className="fas fa-trash"></i> Hapus
                          </>
                        )}
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default ResidentList;
