// components/Dashboard.js
import React from 'react';

const Dashboard = ({ navigateTo }) => {
  const menuItems = [
    {
      icon: 'fas fa-user-plus',
      title: 'Input Data Oma & Opa',
      description: 'Tambahkan data penghuni baru ke dalam sistem',
      page: 'input'
    },
    {
      icon: 'fas fa-users',
      title: 'Daftar Opa & Oma',
      description: 'Lihat daftar semua penghuni panti',
      page: 'list'
    },
    {
      icon: 'fas fa-clipboard-list',
      title: 'Record Harian Opa & Oma',
      description: 'Catat aktivitas dan kondisi harian',
      page: 'record'
    },
    {
      icon: 'fas fa-hand-holding-heart',
      title: 'Kelola Sumbangan',
      description: 'Kelola pemasukan dan pengeluaran dana',
      page: 'donation'
    }
  ];

  return (
    <div className="page-wrapper" style={{ background: 'white', borderRadius: '12px', padding: '30px', boxShadow: '0 4px 12px rgba(0,0,0,0.05)' }}>
      <div className="app-header">
        <h1><i className="fas fa-heartbeat"></i> Panti Werdha Kasih Karunia</h1>
        <p className="subtitle">Sistem Monitoring Kesehatan & Kesejahteraan Penghuni</p>
      </div>

      <div className="menu-grid">
        {menuItems.map((item, index) => (
          <div 
            key={index}
            className="menu-item" 
            onClick={() => navigateTo(item.page)}
            style={{
              background: 'white',
              borderRadius: '12px',
              padding: '30px 25px',
              textAlign: 'center',
              cursor: 'pointer',
              transition: 'all 0.3s ease',
              color: '#333',
              position: 'relative',
              overflow: 'hidden',
              border: '1px solid #eaeaea',
              boxShadow: '0 2px 8px rgba(0,0,0,0.05)'
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.transform = 'translateY(-5px)';
              e.currentTarget.style.boxShadow = '0 8px 20px rgba(0,0,0,0.1)';
              e.currentTarget.style.borderColor = '#0056a6';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.transform = 'translateY(0)';
              e.currentTarget.style.boxShadow = '0 2px 8px rgba(0,0,0,0.05)';
              e.currentTarget.style.borderColor = '#eaeaea';
            }}
          >
            <i 
              className={item.icon} 
              style={{
                fontSize: '3.5rem',
                marginBottom: '20px',
                display: 'block',
                color: '#0056a6'
              }}
            ></i>
            <h3 style={{
              fontSize: '1.4rem',
              fontWeight: '600',
              marginBottom: '10px',
              color: '#0056a6'
            }}>{item.title}</h3>
            <p style={{
              fontSize: '0.95rem',
              color: '#6c757d',
              margin: 0
            }}>{item.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
