// client/src/components/ResidentList.js (updated)
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { calculateAge, formatDate } from '../utils/helpers';

const ResidentList = ({ navigateTo, showDetail }) => {
  const [residents, setResidents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchResidents();
  }, []);

  const fetchResidents = async () => {
    try {
      const response = await axios.get('/api/residents');
      setResidents(response.data);
    } catch (error) {
      console.error('Error fetching residents:', error);
    } finally {
      setLoading(false);
    }
  };

  // Filter residents based on search term
  const filteredResidents = residents.filter(resident => 
    resident.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="page-wrapper">
      <button className="btn btn-back" onClick={() => navigateTo('dashboard')}>
        <i className="fas fa-arrow-left"></i> Kembali
      </button>

      <h2 className="page-title">
        <i className="fas fa-users"></i>
        Daftar Opa & Oma
      </h2>

      <div className="search-box">
        <input 
          type="text" 
          className="form-control" 
          placeholder="ðŸ” Cari nama Opa atau Oma..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {loading ? (
        <div className="text-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">Memuat data...</p>
        </div>
      ) : filteredResidents.length === 0 ? (
        <div className="text-center py-5">
          <i className="fas fa-users fa-3x text-muted mb-3"></i>
          <p className="text-muted">
            {searchTerm ? 'Tidak ada penghuni yang sesuai dengan pencarian' : 'Belum ada data penghuni'}
          </p>
          {!searchTerm && (
            <button 
              className="btn btn-primary-custom mt-3"
              onClick={() => navigateTo('input')}
            >
              <i className="fas fa-user-plus"></i> Tambah Penghuni Baru
            </button>
          )}
        </div>
      ) : (
        <div className="profile-grid">
          {filteredResidents.map(resident => {
            const age = calculateAge(resident.birth_date);
            const genderIcon = resident.gender === 'L' ? 'fa-male' : 'fa-female';
            const type = resident.gender === 'L' ? 'Opa' : 'Oma';
            
            return (
              <div 
                key={resident.id} 
                className="profile-card" 
                onClick={() => showDetail(resident.id)}
                style={{ cursor: 'pointer' }}
              >
                <div className="profile-header">
                  <div className="profile-avatar">
                    <i className={`fas ${genderIcon}`}></i>
                  </div>
                  <h5 style={{ margin: 0, fontSize: '1.3rem' }}>{resident.name}</h5>
                  <p style={{ margin: '5px 0 0 0', opacity: 0.9 }}>
                    {type} â€¢ {age || '?'} Tahun
                  </p>
                </div>
                <div className="profile-body">
                  <div className="profile-info-item">
                    <i className="fas fa-heartbeat"></i>
                    <span><strong>Kondisi:</strong> {resident.health_condition || 'Tidak ada data'}</span>
                  </div>
                  <div className="profile-info-item">
                    <i className="fas fa-calendar"></i>
                    <span><strong>Masuk:</strong> {formatDate(resident.join_date) || '-'}</span>
                  </div>
                  <div className="mt-3">
                    <span className={`badge ${resident.health_condition === 'Kurang Sehat' ? 'bg-warning text-dark' : 'bg-success'} badge-custom`}>
                      {resident.health_condition === 'Kurang Sehat' ? 'Perlu Perhatian' : 'Aktif'}
                    </span>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default ResidentList;
