// components/Record.js
import React, { useState, useEffect } from 'react';
import { recordsAPI, residentsAPI, activityTypesAPI } from '../services/api';

const Record = ({ navigateTo }) => {
  const [showRecordForm, setShowRecordForm] = useState(false);
  const [records, setRecords] = useState([]);
  const [residents, setResidents] = useState([]);
  const [activityTypes, setActivityTypes] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isFormLoading, setIsFormLoading] = useState(false);
  
  const [filters, setFilters] = useState({
    resident: '',
    date_from: '',
    date_to: ''
  });

  const [formData, setFormData] = useState({
    resident_id: '',
    activity_type_id: '',
    record_datetime: new Date().toISOString().slice(0, 16),
    condition: 'Baik',
    notes: '',
    recorded_by: 'Staff'
  });

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    fetchRecords();
  }, [filters]);

  const fetchData = async () => {
    try {
      setIsLoading(true);
      const [residentsData, activityTypesData] = await Promise.all([
        residentsAPI.getAll({ status: 'Aktif' }),
        activityTypesAPI.getAll()
      ]);
      setResidents(residentsData);
      setActivityTypes(activityTypesData);
    } catch (error) {
      console.error('Error fetching data:', error);
      alert('Gagal memuat data');
    } finally {
      setIsLoading(false);
    }
  };

  const fetchRecords = async () => {
    try {
      setIsLoading(true);
      const data = await recordsAPI.getAll(filters);
      setRecords(data);
    } catch (error) {
      console.error('Error fetching records:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const toggleForm = () => {
    setShowRecordForm(!showRecordForm);
    if (!showRecordForm) {
      setFormData({
        resident_id: '',
        activity_type_id: '',
        record_datetime: new Date().toISOString().slice(0, 16),
        condition: 'Baik',
        notes: '',
        recorded_by: 'Staff'
      });
    }
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmitRecord = async (e) => {
    e.preventDefault();
    setIsFormLoading(true);

    try {
      await recordsAPI.create(formData);
      alert('✅ Record berhasil disimpan!');
      setShowRecordForm(false);
      fetchRecords(); // Refresh the list
    } catch (error) {
      console.error('Error creating record:', error);
      alert('❌ Gagal menyimpan record: ' + (error.error || 'Terjadi kesalahan'));
    } finally {
      setIsFormLoading(false);
    }
  };

  const handleFilterChange = (field, value) => {
    setFilters(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const getConditionColor = (condition) => {
    switch (condition) {
      case 'Baik': return 'success';
      case 'Cukup Baik': return 'warning';
      case 'Kurang Baik': return 'danger';
      default: return 'secondary';
    }
  };

  const formatDateTime = (datetime) => {
    const date = new Date(datetime);
    return date.toLocaleString('id-ID', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="page-wrapper">
      <button className="btn btn-back" onClick={() => navigateTo('dashboard')}>
        <i className="fas fa-arrow-left"></i> Kembali
      </button>

      <h2 className="page-title">
        <i className="fas fa-clipboard-list"></i>
        Record Harian Opa & Oma
      </h2>

      <div className="mb-4 d-flex justify-content-between">
        <button className="btn btn-primary-custom" onClick={toggleForm}>
          <i className="fas fa-plus"></i> Tambah Record Baru
        </button>
        <button className="btn btn-outline-secondary" onClick={fetchData}>
          <i className="fas fa-sync-alt"></i> Refresh
        </button>
      </div>

      {/* Form Record */}
      {showRecordForm && (
        <div className="form-section">
          <h4><i className="fas fa-edit"></i> Form Record Harian</h4>
          <form onSubmit={handleSubmitRecord}>
            <div className="row g-3">
              <div className="col-md-6">
                <label className="form-label">Pilih Opa/Oma *</label>
                <select 
                  className="form-select" 
                  name="resident_id"
                  value={formData.resident_id}
                  onChange={handleFormChange}
                  required
                  disabled={isFormLoading}
                >
                  <option value="">-- Pilih Penghuni --</option>
                  {residents.map(resident => (
                    <option key={resident.id} value={resident.id}>
                      {resident.name} ({resident.type}, {resident.age} tahun)
                    </option>
                  ))}
                </select>
              </div>
              <div className="col-md-6">
                <label className="form-label">Tanggal & Waktu *</label>
                <input 
                  type="datetime-local" 
                  className="form-control" 
                  name="record_datetime"
                  value={formData.record_datetime}
                  onChange={handleFormChange}
                  required 
                  disabled={isFormLoading}
                />
              </div>
              <div className="col-md-6">
                <label className="form-label">Jenis Kegiatan *</label>
                <select 
                  className="form-select" 
                  name="activity_type_id"
                  value={formData.activity_type_id}
                  onChange={handleFormChange}
                  required
                  disabled={isFormLoading}
                >
                  <option value="">-- Pilih Kegiatan --</option>
                  {activityTypes.map(type => (
                    <option key={type.id} value={type.id}>
                      {type.name}
                    </option>
                  ))}
                </select>
              </div>
              <div className="col-md-6">
                <label className="form-label">Kondisi Umum *</label>
                <select 
                  className="form-select" 
                  name="condition"
                  value={formData.condition}
                  onChange={handleFormChange}
                  required
                  disabled={isFormLoading}
                >
                  <option value="Baik">Baik</option>
                  <option value="Cukup Baik">Cukup Baik</option>
                  <option value="Kurang Baik">Kurang Baik</option>
                </select>
              </div>
              <div className="col-12">
                <label className="form-label">Keterangan / Catatan *</label>
                <textarea 
                  className="form-control" 
                  name="notes"
                  value={formData.notes}
                  onChange={handleFormChange}
                  rows="3" 
                  placeholder="Tuliskan detail kegiatan..."
                  required
                  disabled={isFormLoading}
                ></textarea>
              </div>
            </div>
            <div className="text-center mt-4">
              <button 
                type="submit" 
                className="btn btn-primary-custom me-2"
                disabled={isFormLoading}
              >
                {isFormLoading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2"></span>
                    Menyimpan...
                  </>
                ) : (
                  <>
                    <i className="fas fa-save"></i> Simpan
                  </>
                )}
              </button>
              <button 
                type="button" 
                className="btn btn-secondary-custom" 
                onClick={toggleForm}
                disabled={isFormLoading}
              >
                <i className="fas fa-times"></i> Batal
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Filters */}
      <div className="filter-row mb-4">
        <select 
          className="form-select"
          value={filters.resident}
          onChange={(e) => handleFilterChange('resident', e.target.value)}
          disabled={isLoading}
        >
          <option value="">Semua Opa & Oma</option>
          {residents.map(resident => (
            <option key={resident.id} value={resident.id}>
              {resident.name}
            </option>
          ))}
        </select>
        <input 
          type="date" 
          className="form-control"
          value={filters.date_from}
          onChange={(e) => handleFilterChange('date_from', e.target.value)}
          placeholder="Dari tanggal"
          disabled={isLoading}
        />
        <input 
          type="date" 
          className="form-control"
          value={filters.date_to}
          onChange={(e) => handleFilterChange('date_to', e.target.value)}
          placeholder="Sampai tanggal"
          disabled={isLoading}
        />
        <button 
          className="btn btn-outline-secondary"
          onClick={() => setFilters({ resident: '', date_from: '', date_to: '' })}
          disabled={isLoading}
        >
          <i className="fas fa-times"></i> Reset
        </button>
      </div>

      {/* Record List */}
      <div className="record-list">
        {isLoading ? (
          <div className="text-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p className="mt-2">Memuat data record...</p>
          </div>
        ) : records.length === 0 ? (
          <div className="text-center py-5">
            <i className="fas fa-clipboard-list fa-3x text-muted mb-3"></i>
            <p className="text-muted">
              {Object.values(filters).some(f => f) 
                ? 'Tidak ditemukan record dengan filter tersebut' 
                : 'Belum ada record harian. Tambahkan record baru untuk melihatnya di sini.'
              }
            </p>
          </div>
        ) : (
          records.map(record => (
            <div key={record.id} className="record-card">
              <div className="record-header">
                <div className="record-date">
                  <i className="fas fa-calendar-day"></i> {formatDateTime(record.record_datetime)}
                </div>
                <span className={`badge bg-${record.activity_color || 'primary'}`}>
                  {record.activity_name}
                </span>
              </div>
              <h5 className="record-name">
                {record.resident_name} ({record.resident_type})
              </h5>
              <div className="mb-2">
                <span className={`badge bg-${getConditionColor(record.condition)} me-2`}>
                  {record.condition}
                </span>
              </div>
              <p className="mb-0">{record.notes}</p>
              {record.recorded_by && (
                <div className="record-footer mt-2">
                  <small className="text-muted">
                    Dicatat oleh: {record.recorded_by}
                  </small>
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Record;
